<?php

namespace AppBundle\Repository;

use AppBundle\Entity\Asset\IssueStatus;

/**
 * TransferStatusRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class TransferStatusRepository extends \Doctrine\ORM\EntityRepository
{

    public function findAll()
    {
        return $this->getEntityManager()
                        ->createQuery(
                                "SELECT ts FROM AppBundle\Entity\Asset\TransferStatus ts ORDER BY ts.name ASC"
                        )
                        ->getResult();
    }

}
